import React from 'react'
import { Link } from 'react-router-dom'

const Sidebar = () => {
    return (
        <main className="d-flex flex-nowrap">
            <div className="d-flex flex-column flex-shrink-0 p-1 bg-light" style={{ width: "180px" }}>
                <ul className="nav nav-pills flex-column mb-auto">
                    <li>
                        <a href="#" className="nav-link link-dark">
                            <i className="fa fa-tachometer fa-2xl me-2" style={{width: "16px", height: "16px"}}></i>Dashboard
                        </a>
                    </li>
                    <li>
                        <a href="#" className="nav-link link-dark">
                            <i className="fa fa-regular fa-basket-shopping"></i>
                            Orders
                        </a>
                    </li>
                    <li>
                        <a href="#" className="nav-link link-dark">

                            Products
                        </a>
                    </li>
                    <li>
                        <a href="#" className="nav-link link-dark">

                            Customers
                        </a>
                    </li>
                </ul>
                <hr />
                <div className="dropdown">
                    <a href="#" className="d-flex align-items-center link-dark text-decoration-none dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                        <img src="https://github.com/mdo.png" alt="" width="32" height="32" className="rounded-circle me-2" />
                        <strong>mdo</strong>
                    </a>
                    <ul className="dropdown-menu text-small shadow">
                        <li><a className="dropdown-item" href="#">New project...</a></li>
                        <li><a className="dropdown-item" href="#">Settings</a></li>
                        <li><a className="dropdown-item" href="#">Profile</a></li>
                        <li><hr className="dropdown-divider" /></li>
                        <li><a className="dropdown-item" href="#">Sign out</a></li>
                    </ul>
                </div>
            </div>
        </main>
        // <div className="sidebar-wrapper">
