import React, { Fragment, useEffect } from 'react'
import Loader from '../layouts/Loader'
import MetaData from '../layouts/MetaData'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'
import { getProductDetails, clearErrors } from '../../actions/productActions'

const ProductDetails = ({ match }) => {

    const dispatch = useDispatch();
    const alert = useAlert();
    const { loading, error, product } = useSelector(state => state.ProductDetails)

    useEffect(() => {
        dispatch(getProductDetails(match.params.id))

        if (error) {
            alert.error(error);
            dispatch(clearErrors())
        }
    }, [dispatch, alert, error, match.params.id])

    return (
        <Fragment>
            {loading ? <Loader /> : (
                <Fragment>
                    <div className="container container-fluid">
                        <div className="row f-flex justify-content-around">
                            <div className="col-md-6 col-lg-5">
                                <div className="card border-0 p-3 m-2">
                                    <img className="img-fluid m-3" src="https://res.cloudinary.com/dh3m4os9t/image/upload/v1654302337/succulents/Nevada_P60_qwl9kp.webp"></img>
                                </div>
                            </div>
                            <div className="col-md-6">
                                <div className="p-3" style={{ textAlign: "left", letterSpacing: ".1rem" }}>
                                    <h1>{product.name}</h1>
